DROP TABLE IF EXISTS db_areas;
CREATE TABLE `db_areas` (
  `id_areas` int(11) NOT NULL AUTO_INCREMENT,
  `areas` varchar(50) NOT NULL,
  PRIMARY KEY (`id_areas`)
) ENGINE=InnoDB AUTO_INCREMENT=175 DEFAULT CHARSET=latin1;
DROP TABLE IF EXISTS db_estado;
CREATE TABLE `db_estado` (
  `id_estado` int(11) NOT NULL AUTO_INCREMENT,
  `estado` varchar(50) NOT NULL,
  PRIMARY KEY (`id_estado`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=latin1;
DROP TABLE IF EXISTS db_histo;
CREATE TABLE `db_histo` (
  `id_histo` int(11) NOT NULL AUTO_INCREMENT,
  `origen` varchar(50) NOT NULL,
  `referencia` varchar(50) NOT NULL,
  `numero` varchar(50) NOT NULL,
  `obs` varchar(300) NOT NULL,
  `fecha_recibido` varchar(50) NOT NULL,
  `estado` varchar(50) NOT NULL,
  `destino` varchar(50) NOT NULL,
  `update` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `obs2` varchar(300) NOT NULL,
  `id_notas` varchar(50) NOT NULL,
  PRIMARY KEY (`id_histo`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;
DROP TABLE IF EXISTS db_notas;
CREATE TABLE `db_notas` (
  `id_notas` int(11) NOT NULL AUTO_INCREMENT,
  `origen` varchar(50) NOT NULL,
  `referencia` varchar(50) NOT NULL,
  `numero` varchar(30) NOT NULL,
  `obs` varchar(500) NOT NULL,
  `fecha_recibido` varchar(10) NOT NULL,
  `estado` varchar(50) NOT NULL,
  `destino` varchar(50) NOT NULL,
  `update` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' ON UPDATE CURRENT_TIMESTAMP,
  `usuario` varchar(500) NOT NULL,
  `vence` varchar(30) NOT NULL,
  PRIMARY KEY (`id_notas`)
) ENGINE=InnoDB AUTO_INCREMENT=2122 DEFAULT CHARSET=latin1;
DROP TABLE IF EXISTS db_usuario;
CREATE TABLE `db_usuario` (
  `id_usuario` int(11) NOT NULL AUTO_INCREMENT,
  `usuario` varchar(50) NOT NULL,
  `pas` varchar(50) NOT NULL,
  `privilegio` int(3) NOT NULL,
  PRIMARY KEY (`id_usuario`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=latin1;
